import React from "react"
import { cva, type VariantProps } from "class-variance-authority"
import { AlertOctagonIcon } from "lucide-react"

import { cn } from "@/lib/utils"

const errorVariants = cva("flex items-start text-red-900", {
  variants: {
    size: {
      small: "text-[13px]",
      medium: "text-sm",
      large: "text-base",
    },
  },
  defaultVariants: {
    size: "medium",
  },
})

interface ErrorProps extends VariantProps<typeof errorVariants> {
  children: React.ReactNode
}

const Error: React.FC<ErrorProps> = ({ size = "medium", children }) => {
  return (
    <div className={cn(errorVariants({ size }))}>
      <AlertOctagonIcon className="mr-2 mt-0.5 h-4 w-4" />
      <p className="[word-break:break-word]">{children}</p>
    </div>
  )
}

export { Error }
