import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const inputVariants = cva(
  "border flex border-gray-alpha-400 bg-background-100",
  {
    variants: {
      size: {
        small: "h-8 text-sm rounded-md",
        medium: "h-10 text-sm rounded-md",
        large: "h-12 text-base rounded-lg",
      },
    },
    defaultVariants: {
      size: "medium",
    },
  }
)

export interface InputProps
  extends Omit<React.InputHTMLAttributes<HTMLInputElement>, "size">,
    VariantProps<typeof inputVariants> {}

const Input = React.forwardRef<HTMLInputElement, InputProps>(
  ({ className, type, size, ...props }, ref) => {
    return (
      <div className={cn(inputVariants({ size, className }))}>
        <input
          type={type}
          className={cn("px-3 bg-transparent placeholder:text-gray-700")}
          ref={ref}
          {...props}
        />
      </div>
    )
  }
)
Input.displayName = "Input"

export { Input }
