"use client"

import * as React from "react"
import { VariantProps, cva } from "class-variance-authority"
import { InfoIcon } from "lucide-react"

import { cn } from "@/lib/utils"

const noteVariants = cva(
  "border p-2 flex justify-between items-center rounded-md gap-3 [word-break:break-word] grow",
  {
    variants: {
      variant: {
        default: "[--note-color:red-500]",
      },
      size: {
        small: "py-1.5 px-2",
        medium: "py-2 px-3",
        large: "",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "medium",
    },
  }
)
interface NoteProps {
  children: React.ReactNode
  action?: React.ReactNode
  size?: VariantProps<typeof noteVariants>["size"]
  type?: VariantProps<typeof noteVariants>["variant"]
  fill?: boolean
}

const Note = ({ action, children, size, type }: NoteProps) => {
  return (
    <div className={cn(noteVariants({ variant: type, size }))}>
      <span className="flex items-center gap-3">
        <span>
          <InfoIcon className="w-4 h-4" />
        </span>
        <span>{children}</span>
      </span>
      {action && <div className="">{action}</div>}
    </div>
  )
}

export { Note }
