type SpinnerProps = { size?: string }

const Spinner = ({ ...props }: SpinnerProps) => {
  const className = "bg-gray-700 rounded-[5px]"

  return (
    <div className="w-5 h-5">
      <div className="w-5 h-5 relative top-1/2 left-1/2">
        {Array(12)
          .fill(null)
          .map((_, i) => (
            <div
              key={i}
              className="bg-gray-700 rounded-[5px] w-[24%] h-[8%] absolute left-[10%] top-[3.9%] animate-spinner-spin"
              style={{ transform: `rotate(${i * 30}deg) translate(146%)` }}
            />
          ))}
      </div>
    </div>
  )
}

export { Spinner }
